mapper.insert:
INSERT INTO tb_member (

) VALUES (
      #{id}
    , #{loginId}
    , #{password}
    , #{name}
    , #{gender}
    , #{birthday}
    , 0
    , NOW()
    , NULL
)

mapper.update:
UPDATE tb_member
SET
      modified_date = NOW()
    , name = #{name}
    , gender = #{gender}
    , birthday = #{birthday}

    , password = #{password}

WHERE
    id = #{id}
